var duration = 2000;

// Calculate the number of steps and the time interval for each step
var steps = 60; // Number of steps for the animation
var interval = duration / steps;

// Start the animation
animateLocation(0);

function animateLocation(step) {

  if (step <= steps) {
    // Calculate progress from 0 to 1
    var progress = step / steps;

    // Apply easing function to the progress value
    var easedProgress = cubicEaseInOut(progress);

    // Interpolate latitude and longitude using the eased progress
    Outputs.newLat = lerp(startLat, endLat, easedProgress);
    Outputs.newLon = lerp(startLon, endLon, easedProgress);

    // Schedule the next step of the animation
    setTimeout(() => animateLocation(step + 1), interval);
  } else {
    // Call newLocation at the end of the animation
    Outputs.newLocation();

  }
}

// Linear interpolation function
function lerp(start, end, progress) {
  return start + (end - start) * progress;
}

function cubicEaseInOut(t) {
  return t < 0.5 ? 4 * t * t * t : 1 - Math.pow(-2 * t + 2, 3) / 2;
}

