    let canvas = document.createElement("div");
    Node.Inputs.group.getDOMElement().appendChild(canvas);
    var array = Node.Inputs.itemLocations;

    sketch = function(p) {
        p.preload = function() {
            var inputMapData = Node.Inputs.MapboxData;
            mapImage = p.loadImage(inputMapData);
        };

        p.setup = function(){
            p.createCanvas(Node.Inputs.width, Node.Inputs.height);   
            p.drawingContext.canvas.willReadFrequently = true; 
        }

        p.draw = function() {
            if (showMap) {
                //p.image(mapImage, 0, 0, Node.Inputs.width, Node.Inputs.height);
                var img = mapImage;
                img.resize(Node.Inputs.width, Node.Inputs.height);

                if (revealedPixels < maxRevealedPixels) {   
                        let gridWidth = Math.ceil(img.width / resolution);
                        let gridHeight = Math.ceil(img.height / resolution);

                        let xIndex = Math.floor(p.random(gridWidth));
                        let yIndex = Math.floor(p.random(gridHeight));

                        let xPos = xIndex * resolution;
                        let yPos = yIndex * resolution;

                        let colorAtPixel = img.get(xPos, yPos);
                        if(p.brightness(colorAtPixel) < 80){ // filter out white
                            p.fill(colorAtPixel);
                            p.noStroke();
                            p.rect(xPos, yPos, resolution, resolution);
                        }
                        revealedPixels++;
                }
            }else{
                p.clear();
            }            
        }

    };
    p = new p5(sketch, canvas);

}

Node.Signals.reLoad = function(){
    showMap = true;
    var inputMapData = Node.Inputs.MapboxData;
    mapImage = p.loadImage(inputMapData);
}

