        var imgSrc = Inputs.imgSrc
        console.log(imgSrc)

        // fetch png img from src
        fetch(imgSrc)
          .then((response) => response.blob())
          .then((blob) => {
            let img = new Image()
            img.src = URL.createObjectURL(blob)
            img.onload = () => {
              p5Image = p.createImage(img.width, img.height)
              p5Image.drawingContext.drawImage(img, 0, 0)
              p5Image = p.cropRandomCorner(p5Image, p5Image.width/2, p5Image.height/2);
