    }
    p.pixelateImage = function (img, resolution) {
      // Calculate the size of each square based on the input resolution
      let squareWidth = img.width / resolution;
      let squareHeight = img.height / resolution;

      // Iterate through the image and sample the color at each step based on the input resolution
      for (let y = 0; y < img.height; y += squareHeight) {
        for (let x = 0; x < img.width; x += squareWidth) {
          let centerX = x + squareWidth / 2;
          let centerY = y + squareHeight / 2;

          // Get the color at the center of the square
          let color = img.get(centerX, centerY);
          // Set the fill color to the sampled color
          p.noStroke();
          p.fill(color);
          //p.ellipse(x, y, squareWidth+1, squareHeight+1);
          p.rect(x, y, squareWidth+1, squareHeight+1);
        }
      }
    }

    p.cropRandomCorner = function (img, cropWidth, cropHeight) {
      // Ensure the crop dimensions are smaller than the original image dimensions
      if (cropWidth > img.width || cropHeight > img.height) {
        console.error("Crop dimensions are larger than the original image dimensions.");
        return img;
      }

      // Randomly select one of the four corners
      let corner = Math.floor(p.random(4));

      let x, y;

      switch (corner) {
        case 0: // Top-left corner
          x = 0;
          y = 0;
          break;
        case 1: // Top-right corner
          x = img.width - cropWidth;
          y = 0;
          break;
        case 2: // Bottom-left corner
          x = 0;
          y = img.height - cropHeight;
          break;
        case 3: // Bottom-right corner
          x = img.width - cropWidth;
          y = img.height - cropHeight;
          break;
      }

      // Get the cropped section of the image
      let croppedImage = img.get(x, y, cropWidth, cropHeight);

      croppedImage.resize(Inputs.width, Inputs.height);
      return croppedImage;
    }

    p.windowResized = function () {
      p.resizeCanvas(Inputs.width, Inputs.height)
    }
  }

  p = new p5(sketch, canvas)
